package automationday1.copy;

public class For_loops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// TODO Auto-generated method stub
				//Print 2 to -10
				
				int a = 2;
				
				while(a>=-10) {
					System.out.println("The value of a ="+ a);
					
					a--;
				}
				
					// print 3to 10
				int k = 3;
				
				do {
					System.out.println("The value of k=" +k);
					k++;
					
				}while(k<=10);
				
			}

		
				
				}
				
