package functionality;

public class While_loop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = -500;
		
		while(i<=-455) {
			System.out.println("i="+ i);
		
			if (i==-480) {
				System.out.println("we got the value");
				break;
			}
			i++;
		}	
	}

}
